import{_ as p,o as e,c as t,f as o,a as c,d as a}from"./app.9a681789.js";const s={},_=c("p",null,"\u6559\u80B2\u6216\u6587\u5316\u7684\u76EE\u7684\uFF0C\u4E0D\u5916\u4E4E\u662F\u53D1\u5C55\u77E5\u8BC6\u4E0A\u7684\u9274\u8D4F\u529B\u548C\u884C\u4E3A\u4E0A\u7684\u826F\u597D\u8868\u73B0\u3002\u6709\u6559\u517B\u7684\u4EBA\uFF0C\u6216\u53D7\u8FC7\u7406\u60F3\u6559\u80B2\u7684\u4EBA\uFF0C\u4E0D\u4E00\u5B9A\u662F\u4E2A\u535A\u5B66\u7684\u4EBA\uFF0C\u800C\u662F\u4E2A\u77E5\u9053\u4F55\u6240\u7231\u3001\u4F55\u6240\u6076\u7684\u4EBA\u3002",-1),n=a("<p>\u4E00\u4E2A\u4EBA\u80FD\u591F\u77E5\u9053\u4F55\u6240\u7231\u4F55\u6240\u6076\uFF0C\u4FBF\u662F\u5C1D\u5230\u4E86\u77E5\u8BC6\u7684\u6ECB\u5473\u3002\u4E16\u754C\u4E0A\u6709\u4E00\u4E9B\u4EBA\uFF0C\u5FC3\u91CC\u585E\u6EE1\u5386\u53F2\u4E0A\u7684\u65E5\u671F\u548C\u4EBA\u7269\uFF0C\u5BF9\u4FC4\u56FD\u6216\u6377\u514B\u65F6\u4E8B\u6781\u4E3A\u719F\u8BC6\uFF0C\u53EF\u662F\u4ED6\u4EEC\u7684\u6001\u5EA6\u6216\u89C2\u70B9\u662F\u5B8C\u5168\u9519\u8BEF\u7684\uFF1B\u5728\u793E\u4EA4\u96C6\u4F1A\u91CC\u78B0\u5230\u8FD9\u4E48\u4E00\u4E2A\u4EBA\uFF0C\u771F\u662F\u518D\u6C14\u715E\u4EBA\u4E5F\u6CA1\u6709\u7684\u4E8B\u4E86\u3002</p><p>\u6211\u66FE\u78B0\u89C1\u8FC7\u8FD9\u79CD\u4EBA\uFF0C\u89C9\u5F97\u8C08\u8BDD\u4E2D\u65E0\u8BBA\u8BB2\u5230\u4EC0\u4E48\u8BDD\u9898\uFF0C\u4ED6\u4EEC\u603B\u6709\u4E00\u4E9B\u4E8B\u5B9E\u6216\u6570\u5B57\u53EF\u4EE5\u63D0\u51FA\u6765\uFF0C\u53EF\u662F\u4ED6\u4EEC\u7684\u89C1\u89E3\u662F\u4EE4\u4EBA\u6C14\u77ED\u7684\u3002\u8FD9\u79CD\u4EBA\u6709\u5E7F\u535A\u5B66\u95EE\uFF0C\u53EF\u662F\u7F3A\u4E4F\u89C1\u8BC6\u6216\u9274\u8D4F\u529B\u3002\u535A\u5B66\u4EC5\u662F\u585E\u6EE1\u4E00\u4E9B\u4E8B\u5B9E\u6216\u89C1\u95FB\u800C\u5DF2\uFF0C\u53EF\u662F\u9274\u8D4F\u529B\u6216\u89C1\u8BC6\u5374\u662F\u57FA\u4E8E\u827A\u672F\u7684\u5224\u65AD\u529B\u3002</p><p>\u4E2D\u56FD\u4EBA\u8BB2\u5230\u5B66\u8005\u65F6\uFF0C\u666E\u901A\u662F\u5206\u4E3A\u5B66\u3001\u884C\u3001\u8BC6\u3002\u5BF9\u5386\u53F2\u5B66\u5BB6\uFF0C\u5C24\u5176\u4EE5\u8FD9\u4E09\u70B9\u4E3A\u6279\u8BC4\u6807\u51C6\uFF1B\u4E00\u90E8\u5386\u53F2\uFF0C\u4E5F\u8BB8\u5199\u5F97\u6781\u4E3A\u6E0A\u535A\uFF0C\u53EF\u5B8C\u5168\u6CA1\u6709\u89C1\u8BC6\uFF0C\u5728\u6279\u5224\u5386\u53F2\u4E0A\u4EBA\u7269\u4E8B\u8FF9\u65F6\uFF0C\u4F5C\u8005\u4E5F\u8BB8\u6CA1\u6709\u4E00\u70B9\u72EC\u51FA\u5FC3\u88C1\u7684\u89C1\u89E3\u6216\u6DF1\u523B\u7684\u7406\u89E3\u529B\u3002\u8981\u89C1\u95FB\u5E7F\u535A\uFF0C\u8981\u641C\u96C6\u4E8B\u5B9E\u548C\u8BE6\u60C5\uFF0C\u4E43\u662F\u6700\u5BB9\u6613\u7684\u4E8B\u3002\u4EFB\u4F55\u4E00\u4E2A\u5386\u53F2\u65F6\u4EE3\u90FD\u6709\u8BB8\u591A\u4E8B\u5B9E\uFF0C\u6211\u4EEC\u8981\u5C06\u4E4B\u585E\u6EE1\u5FC3\u4E2D\uFF0C\u662F\u5F88\u5BB9\u6613\u7684\uFF1B\u53EF\u662F\u9009\u62E9\u91CD\u8981\u4E8B\u5B9E\u65F6\u6240\u9700\u8981\u7684\u89C1\u8BC6\uFF0C\u5374\u662F\u6BD4\u8F83\u56F0\u96BE\u7684\u4E8B\uFF0C\u56E0\u4E3A\u8FD9\u8981\u770B\u4E2A\u4EBA\u89C2\u70B9\u5982\u4F55\u3002</p><p>\u4E00\u4E2A\u4EBA\u5FC5\u987B\u80FD\u591F\u5BFB\u6839\u7A76\u5E95\uFF0C\u5FC5\u987B\u5177\u6709\u72EC\u7ACB\u5224\u65AD\u529B\uFF0C\u5FC5\u987B\u4E0D\u53D7\u4EFB\u4F55\u793E\u4F1A\u5B66\u3001\u653F\u6CBB\u5B66\u3001\u6587\u5B66\u3001\u827A\u672F\u6216\u5B66\u7A76\u7684\u80E1\u8BF4\u6240\u5A01\u5413\uFF0C\u624D\u80FD\u591F\u6709\u9274\u8D4F\u529B\u6216\u89C1\u8BC6\u3002</p><p>\u6211\u4EEC\u6210\u4EBA\u7684\u751F\u6D3B\uFF0C\u65E0\u7591\u53D7\u7740\u8BB8\u591A\u80E1\u8BF4\u548C\u9A97\u4EBA\u7684\u4E1C\u897F\u5305\u56F4\uFF1A\u540D\u8A89\u7684\u80E1\u8BF4\uFF0C\u8D22\u5BCC\u7684\u80E1\u8BF4\uFF0C\u7231\u56FD\u7684\u80E1\u8BF4\uFF0C\u653F\u6CBB\u7684\u80E1\u8BF4\uFF0C\u5B97\u6559\u7684\u80E1\u8BF4\uFF0C\u4EE5\u53CA\u9A97\u4EBA\u7684\u8BD7\u4EBA\uFF0C\u9A97\u4EBA\u7684\u827A\u672F\u5BB6\uFF0C\u9A97\u4EBA\u7684\u72EC\u88C1\u8005\u548C\u9A97\u4EBA\u7684\u5FC3\u7406\u5B66\u5BB6\u3002\u7CBE\u795E\u5206\u6790\u5B66\u5BB6\u4F1A\u544A\u8BC9\u6211\u4EEC\u8BF4\uFF1A\u4E00\u4E2A\u4EBA\u513F\u7AE5\u65F6\u4EE3\u7684\u80A0\u80C3\u5B98\u80FD\u6D3B\u52A8\uFF0C\u5BF9\u540E\u6765\u751F\u6D3B\u4E0A\u7684\u91CE\u5FC3\u3001\u8FDB\u53D6\u5FC3\u548C\u8D23\u4EFB\u5FC3\uFF0C\u6709\u5207\u5B9E\u5173\u7CFB\uFF0C\u6216\u8005\u8BF4\u5927\u4FBF\u79D8\u7ED3\u9020\u6210\u4E00\u4E2A\u4EBA\u7684\u541D\u556C\u6027\u60C5\uFF1B\u6709\u89C1\u8BC6\u7684\u4EBA\u542C\u89C1\u8FD9\u79CD\u8BDD\u65F6\uFF0C\u53EA\u597D\u4E00\u7B11\u7F6E\u4E4B\u3002\u4E00\u4E2A\u4EBA\u505A\u9519\u4E86\u4E8B\uFF0C\u4FBF\u662F\u9519\u4E86\uFF0C\u7528\u4E0D\u7740\u62FF\u51FA\u4F1F\u5927\u540D\u8A89\u4EE5\u5A01\u538B\u4EBA\uFF0C\u4E5F\u7528\u4E0D\u7740\u8BF4\u4ED6\u66FE\u8BFB\u8FC7\u8BB8\u591A\u6211\u4EEC\u4E0D\u66FE\u8BFB\u8FC7\u7684\u4E66\uFF0C\u4EE5\u6050\u5413\u4EBA\u3002</p><p>\u6240\u4EE5\uFF0C\u89C1\u8BC6\u548C\u80C6\u91CF\u662F\u6709\u5BC6\u5207\u5173\u7CFB\u7684\uFF0C\u4E2D\u56FD\u4EBA\u5F80\u5F80\u628A\u8BC6\u548C\u80C6\u8FDE\u5728\u4E00\u8D77\uFF1B\u800C\u6211\u4EEC\u77E5\u9053\uFF1A\u80C6\u91CF\u6216\u72EC\u7ACB\u7684\u5224\u65AD\uFF0C\u662F\u4EBA\u7C7B\u4E2D\u4E00\u79CD\u591A\u4E48\u96BE\u5F97\u7684\u7F8E\u5FB7\u3002\u6211\u4EEC\u770B\u89C1\u4E00\u5207\u6709\u7279\u6B8A\u5EFA\u6811\u7684\u601D\u60F3\u5BB6\u548C\u8457\u4F5C\u5BB6\uFF0C\u5728\u5E7C\u5E74\u65F6\u4EE3\uFF0C\u90FD\u6709\u8FD9\u79CD\u667A\u80FD\u4E0A\u7684\u80C6\u91CF\u6216\u72EC\u7ACB\u6027\u3002</p><p>\u8FD9\u79CD\u4EBA\u5982\u679C\u4E0D\u559C\u6B22\u4E00\u4E2A\u8BD7\u4EBA\uFF0C\u4FBF\u8868\u793A\u4E0D\u559C\u6B22\uFF0C\u7EB5\u4F7F\u90A3\u4E2A\u8BD7\u4EBA\u662F\u5F53\u65F6\u6700\u6709\u58F0\u671B\u7684\u8BD7\u4EBA\uFF1B\u5F53\u4ED6\u786E\u5B9E\u559C\u6B22\u4E00\u4E2A\u8BD7\u4EBA\u65F6\uFF0C\u4ED6\u4FBF\u80FD\u591F\u8BF4\u51FA\u559C\u6B22\u4ED6\u7684\u7406\u7531\uFF0C\u56E0\u4E3A\u8FD9\u662F\u4ED6\u5185\u5FC3\u5224\u65AD\u7684\u7ED3\u679C\u3002\u8FD9\u5C31\u662F\u6211\u4EEC\u6240\u8C13\u6587\u5B66\u4E0A\u7684\u9274\u8D4F\u529B\u3002\u5982\u679C\u5F53\u65F6\u76DB\u884C\u7684\u7ED8\u753B\u5B66\u6D3E\u4E3B\u5F20\uFF0C\u4F7F\u4ED6\u7684\u827A\u672F\u672C\u80FD\u611F\u89C9\u4E0D\u5FEB\uFF0C\u4ED6\u4E5F\u4F1A\u52A0\u4EE5\u53CD\u5BF9\u3002\u8FD9\u5C31\u662F\u827A\u672F\u4E0A\u7684\u9274\u8D4F\u529B\u3002</p><p>\u4E00\u79CD\u6D41\u884C\u7684\u54F2\u5B66\u7406\u8BBA\u6216\u65F6\u9AE6\u89C2\u5FF5\uFF0C\u7EB5\u4F7F\u5F97\u5230\u4E00\u4E9B\u6700\u4F1F\u5927\u4EBA\u7269\u7684\u8D5E\u52A9\uFF0C\u4ED6\u4E5F\u4F1A\u8868\u793A\u6F20\u7136\u6001\u5EA6\u3002\u4ED6\u8981\u7B49\u5230\u81EA\u5DF1\u5FC3\u60A6\u8BDA\u670D\uFF0C\u624D\u613F\u76F8\u4FE1\u4E00\u4E2A\u4F5C\u5BB6\u7684\u8BDD\uFF1B\u5982\u679C\u4E00\u4E2A\u4F5C\u5BB6\u80FD\u4F7F\u4ED6\u4FE1\u670D\uFF0C\u90A3\u4E2A\u4F5C\u5BB6\u4FBF\u662F\u5BF9\u7684\uFF0C\u53EF\u662F\uFF0C\u5982\u679C\u90A3\u4E2A\u4F5C\u5BB6\u4E0D\u80FD\u4F7F\u4ED6\u4FE1\u670D\uFF0C\u90A3\u4E48\uFF0C\u4ED6\u81EA\u5DF1\u662F\u5BF9\u7684\uFF0C\u800C\u90A3\u4E2A\u4F5C\u5BB6\u662F\u9519\u7684\u3002\u8FD9\u5C31\u662F\u77E5\u8BC6\u4E0A\u7684\u9274\u8D4F\u529B\u3002</p><p>\u8FD9\u79CD\u667A\u80FD\u4E0A\u7684\u80C6\u91CF\u6216\u72EC\u7ACB\u5224\u65AD\uFF0C\u65E0\u7591\u5730\u9700\u8981\u76F8\u5F53\u5B69\u5B50\u6C14\u7684\u3001\u5929\u771F\u7684\u81EA\u4FE1\u529B\uFF0C\u53EF\u662F\u8FD9\u4E2A\u81EA\u6211\u4FBF\u662F\u4E00\u4E2A\u4EBA\u552F\u4E00\u53EF\u4EE5\u4F9D\u9644\u7684\u4E1C\u897F\uFF0C\u4E00\u4E2A\u7814\u7A76\u8005\u4E00\u65E6\u653E\u5F03\u4E2A\u4EBA\u5224\u65AD\u7684\u6743\u5229\uFF0C\u4FBF\u53EA\u597D\u63A5\u53D7\u4EBA\u751F\u7684\u4E00\u5207\u80E1\u8BF4\u4E86\u3002</p><p>\u5B54\u5B50\u4F3C\u4E4E\u89C9\u5F97\u5B66\u800C\u4E0D\u601D\u6BD4\u601D\u800C\u4E0D\u5B66\u66F4\u5371\u9669\uFF0C\u4ED6\u8BF4\uFF1A\u201C\u5B66\u800C\u4E0D\u601D\u5219\u7F54\uFF0C\u601D\u800C\u4E0D\u5B66\u5219\u6B86\u3002\u201D\u4ED6\u5728\u5F53\u65F6\u4E00\u5B9A\u770B\u89C1\u8FC7\u8BB8\u591A\u5B66\u800C\u4E0D\u601D\u7684\u5B66\u751F\uFF0C\u6240\u4EE5\u624D\u63D0\u51FA\u8FD9\u4E2A\u8B66\u544A\uFF1B\u8FD9\u4E2A\u8B66\u544A\uFF0C\u6B63\u662F\u73B0\u4EE3\u5B66\u6821\u91CC\u6781\u4E3A\u9700\u8981\u7684\u3002\u5927\u5BB6\u90FD\u77E5\u9053\u73B0\u4EE3\u6559\u80B2\u548C\u73B0\u4EE3\u5B66\u6821\u5236\u5EA6\u5927\u62B5\u662F\u9F13\u52B1\u5B66\u751F\u6C42\u5B66\u95EE\uFF0C\u800C\u5FFD\u7565\u9274\u522B\u529B\uFF0C\u540C\u65F6\u8BA4\u4E3A\uFF1A\u628A\u5B66\u8BC6\u586B\u6EE1\u8111\u4E2D\uFF0C\u5C31\u662F\u7EC8\u6781\u76EE\u7684\uFF0C\u597D\u8C61\u5927\u91CF\u5B66\u95EE\uFF0C\u4FBF\u80FD\u591F\u9020\u6210\u4E00\u4E2A\u6709\u6559\u80B2\u7684\u4EBA\u4F3C\u7684\u3002</p><p>\u53EF\u662F\u5B66\u6821\u4E3A\u4EC0\u4E48\u4E0D\u9F13\u52B1\u601D\u60F3\u5462\uFF1F\u6559\u80B2\u5236\u5EA6\u4E3A\u4EC0\u4E48\u628A\u8FFD\u6C42\u5B66\u95EE\u7684\u5FEB\u4E50\uFF0C\u6B6A\u66F2\u6210\u5806\u585E\u5B66\u8BC6\u7684\u673A\u68B0\u5F0F\u3001\u6709\u91CF\u5EA6\u3001\u5343\u7BC7\u4E00\u5F8B\u7684\u3001\u88AB\u52A8\u7684\u5DE5\u4F5C\u5462\uFF1F\u6211\u4EEC\u4E3A\u4EC0\u4E48\u6BD4\u8F83\u6CE8\u91CD\u5B66\u95EE\uFF0C\u800C\u4E0D\u6CE8\u91CD\u601D\u60F3\u5462\uFF1F</p><p>\u6211\u4EEC\u600E\u4E48\u53EF\u4EE5\u56E0\u4E3A\u4E00\u4E2A\u5927\u5B66\u6BD5\u4E1A\u751F\u5FF5\u5B8C\u4E86\u82E5\u5E72\u89C4\u5B9A\u7684\u5FC3\u7406\u5B66\u3001\u4E2D\u53E4\u53F2\u3001\u903B\u8F91\u548C\u201C\u5B97\u6559\u201D\u5B66\u5206\uFF0C\u800C\u4FBF\u79F0\u4ED6\u505A\u53D7\u8FC7\u6559\u80B2\u7684\u4EBA\u5462\uFF1F\u5B66\u6821\u4E3A\u4EC0\u4E48\u8981\u6709\u5206\u6570\u548C\u6587\u51ED\u5462\uFF1F\u5206\u6570\u548C\u6587\u51ED\u5728\u5B66\u751F\u4EEC\u5FC3\u4E2D\u4E3A\u4EC0\u4E48\u4F1A\u4EE3\u66FF\u4E86\u6559\u80B2\u7684\u771F\u76EE\u7684\u5462\uFF1F</p><p>\u7406\u7531\u662F\u5F88\u7B80\u5355\u7684\u3002\u6211\u4EEC\u4E4B\u6240\u4EE5\u6709\u8FD9\u4E2A\u5236\u5EA6\uFF0C\u5C31\u662F\u56E0\u4E3A\u6211\u4EEC\u662F\u5728\u6559\u80B2\u5927\u6279\u4EBA\uFF0C\u50CF\u5DE5\u5382\u91CC\u5927\u91CF\u751F\u4EA7\u4E00\u6837\uFF0C\u800C\u5DE5\u5382\u91CC\u7684\u4E00\u5207\uFF0C\u5FC5\u987B\u4F9D\u4E00\u79CD\u6B7B\u677F\u3001\u673A\u68B0\u7684\u5236\u5EA6\u800C\u8FD0\u884C\u3002</p><p>\u5B66\u6821\u4E3A\u4FDD\u62A4\u5176\u540D\u8A89\uFF0C\u4F7F\u5176\u51FA\u54C1\u6807\u51C6\u5316\u8D77\u89C1\uFF0C\u5FC5\u987B\u4EE5\u6587\u51ED\u4E3A\u8BC1\u660E\u3002\u4E8E\u662F\uFF0C\u6709\u6587\u51ED\u4FBF\u6709\u5206\u7B49\u7EA7\u5FC5\u8981\uFF0C\u6709\u5206\u7B49\u7EA7\u5FC5\u8981\uFF0C\u4FBF\u6709\u5B66\u6821\u5206\u6570\uFF1B\u4E3A\u8981\u7ED9\u5206\u6570\u8D77\u89C1\uFF0C\u5B66\u6821\u5FC5\u987B\u6709\u80CC\u8BF5\u3001\u5927\u8003\u548C\u5C0F\u8003\u3002\u8FD9\u9020\u6210\u4E00\u79CD\u5B8C\u5168\u5408\u7406\u7684\u524D\u56E0\u540E\u679C\uFF0C\u65E0\u6CD5\u53EF\u4EE5\u907F\u514D\u3002</p><p>\u53EF\u662F\u5B66\u6821\u6709\u4E86\u673A\u68B0\u5316\u7684\u5927\u8003\u548C\u5C0F\u8003\uFF0C\u5176\u540E\u679C\u662F\u6BD4\u6211\u4EEC\u60F3\u8C61\u7684\u66F4\u6709\u5BB3\u7684\u3002\u56E0\u4E3A\u8FD9\u4E48\u4E00\u6765\uFF0C\u5B66\u6821\u91CC\u6240\u6CE8\u91CD\u7684\u662F\u201C\u4E8B\u5B9E\u201D\u7684\u8BB0\u5FC6\uFF0C\u800C\u4E0D\u662F\u201C\u9274\u8D4F\u529B\u201D\u6216\u201C\u5224\u65AD\u529B\u201D\u7684\u53D1\u5C55\u4E86\u3002\u6211\u81EA\u5DF1\u4E5F\u66FE\u505A\u8FC7\u6559\u5E08\uFF0C\u6211\u77E5\u9053\u51FA\u4E00\u4E9B\u5173\u4E8E\u5386\u53F2\u65E5\u671F\u7684\u95EE\u9898\uFF0C\u662F\u6BD4\u51FA\u4E00\u4E9B\u542B\u7CCA\u95EE\u9898\u66F4\u5BB9\u6613\u7684\u3002\u540C\u65F6\uFF0C\u6279\u5B9A\u5206\u6570\u4E5F\u6BD4\u8F83\u5BB9\u6613\u3002</p><p>\u8FD9\u4E2A\u5236\u5EA6\u5B9E\u884C\u540E\uFF0C\u6211\u4EEC\u4FBF\u4F1A\u78B0\u5230\u4E00\u79CD\u5371\u9669\uFF0C\u5C31\u662F\u6211\u4EEC\u4F1A\u5FD8\u6389\u6211\u4EEC\u5DF2\u80CC\u5F03\u4E86\u6559\u80B2\u7684\u771F\u7406\u60F3\uFF0C\u6216\u5373\u5C06\u80CC\u5F03\u6559\u80B2\u7684\u771F\u7406\u60F3\uFF1B\u6240\u8C13\u6559\u80B2\u7684\u771F\u7406\u60F3\uFF0C\u6211\u5DF2\u7ECF\u8BF4\u8FC7\uFF0C\u5C31\u662F\u53D1\u5C55\u77E5\u8BC6\u4E0A\u7684\u9274\u8D4F\u529B\u3002</p><p>\u5B54\u5B50\u8BF4\uFF1A\u201C\u8BB0\u95EE\u4E4B\u5B66\uFF0C\u4E0D\u8DB3\u4E3A\u4EBA\u5E08\u3002\u201D\u8FD9\u53E5\u8BDD\u8BB0\u8D77\u6765\u8FD8\u662F\u5F88\u6709\u7528\u3002\u4E16\u95F4\u6CA1\u6709\u6240\u8C13\u5FC5\u4FEE\u79D1\u76EE\uFF0C\u4E5F\u6CA1\u4EC0\u4E48\u4EBA\u4EBA\u5FC5\u8BFB\u4E4B\u4E66\uFF0C\u751A\u81F3\u838E\u58EB\u6BD4\u4E9A\u8457\u4F5C\u4E5F\u4E0D\u662F\u5FC5\u8BFB\u4E4B\u4E66\u3002\u5B66\u6821\u5236\u5EA6\u4E2D\u4F3C\u4E4E\u6709\u4E2A\u611A\u8822\u89C2\u5FF5\uFF0C\u4EE5\u4E3A\u6211\u4EEC\u53EF\u4EE5\u5236\u5B9A\u4E00\u4E9B\u6700\u4F4E\u9650\u5EA6\u7684\u5386\u53F2\u77E5\u8BC6\u6216\u5730\u7406\u77E5\u8BC6\uFF0C\u8981\u505A\u4E00\u4E2A\u53D7\u8FC7\u6559\u80B2\u7684\u4EBA\uFF0C\u4FBF\u975E\u5FF5\u8FD9\u4E9B\u4E1C\u897F\u4E0D\u53EF\u3002</p><p>\u6211\u4EEC\u5FC5\u987B\u653E\u5F03\u201C\u77E5\u8BC6\u53EF\u4EE5\u8861\u91CF\u201D\u7684\u89C2\u5FF5\u3002\u5E84\u5B50\u8BF4\u5F97\u597D\uFF1A\u201C\u543E\u751F\u4E5F\u6709\u6DAF\uFF0C\u800C\u77E5\u4E5F\u65E0\u6DAF\uFF01\u201D\u77E5\u8BC6\u7684\u8FFD\u6C42\uFF0C\u7EC8\u7A76\u662F\u548C\u63A2\u7D22\u4E00\u4E2A\u65B0\u5927\u9646\u4E00\u6837\uFF0C\u6216\u5982\u4F5B\u6717\u58EB\u6240\u8BF4\u201C\u7075\u9B42\u7684\u5192\u9669\u201D\u4E00\u6837\u3002\u5982\u679C\u4E00\u4E2A\u865A\u6000\u82E5\u8C37\u3001\u597D\u95EE\u3001\u597D\u5947\u3001\u5192\u9669\u7684\u5FC3\u667A\uFF0C\u59CB\u7EC8\u4FDD\u6301\u7740\u63A2\u7D22\u7CBE\u795E\uFF0C\u90A3\u4E48\u77E5\u8BC6\u7684\u8FFD\u6C42\u5C31\u4F1A\u6210\u4E3A\u6B22\u4E50\u7684\u4E8B\uFF0C\u800C\u4E0D\u4F1A\u53D8\u6210\u75DB\u82E6\u7684\u5DE5\u4F5C\u3002</p><p>\u6211\u4EEC\u5FC5\u987B\u653E\u5F03\u90A3\u79CD\u6709\u91CF\u5EA6\u3001\u5343\u7BC7\u4E00\u5F8B\u3001\u88AB\u52A8\u7684\u586B\u585E\u89C1\u95FB\u7684\u65B9\u6CD5\uFF0C\u800C\u5B9E\u73B0\u8FD9\u79CD\u79EF\u6781\u3001\u751F\u957F\u7684\u3001\u4E2A\u4EBA\u7684\u6B22\u4E50\u7684\u7406\u60F3\uFF0C\u6587\u51ED\u548C\u5206\u6570\u5236\u5EA6\u4E00\u65E6\u53D6\u6D88\u6216\u4E0D\u88AB\u4EBA\u4EEC\u91CD\u89C6\uFF0C\u77E5\u8BC6\u7684\u8FFD\u6C42\u4FBF\u53EF\u6210\u4E3A\u79EF\u6781\u6D3B\u52A8\uFF0C\u56E0\u4E3A\u5B66\u751F\u81F3\u5C11\u9700\u8981\u95EE\u81EA\u5DF1\u4E3A\u4EC0\u4E48\u8981\u8BFB\u4E66\u3002</p><p>\u5B66\u6821\u73B0\u5728\u5DF2\u7ECF\u66FF\u5B66\u751F\u89E3\u7B54\u8FD9\u4E2A\u95EE\u9898\u4E86\uFF0C\u5B66\u751F\u77E5\u9053\u4ED6\u8BFB\u5927\u5B66\u4E00\u5E74\u7EA7\u7684\u76EE\u7684\uFF0C\u4FBF\u662F\u8981\u505A\u4E8C\u5E74\u7EA7\u751F\uFF1B\u8BFB\u4E8C\u5E74\u7EA7\u7684\u76EE\u7684\uFF0C\u4FBF\u662F\u8981\u505A\u4E09\u5E74\u7EA7\u751F\uFF0C\u5FC3\u4E2D\u4E00\u70B9\u7591\u95EE\u4E5F\u6CA1\u6709\u3002\u8FD9\u4E00\u5207\u5916\u6765\u8BA1\u5212\u90FD\u5E94\u8BE5\u7F6E\u8BF8\u4E0D\u987E\uFF0C\u56E0\u4E3A\u77E5\u8BC6\u8FFD\u6C42\u662F\u4E00\u4E2A\u4EBA\u81EA\u5DF1\u7684\u4E8B\uFF0C\u4E0E\u522B\u4EBA\u65E0\u5E72\u3002</p><p>\u73B0\u5728\u7684\u5B66\u751F\uFF0C\u662F\u4E3A\u6CE8\u518C\u4E3B\u4EFB\u800C\u8BFB\u4E66\uFF0C\u8BB8\u591A\u597D\u5B66\u751F\u5219\u662F\u4E3A\u7236\u6BCD\u3001\u6559\u5E08\u6216\u672A\u6765\u7684\u59BB\u5B50\u800C\u8BFB\u4E66\uFF0C\u4F7F\u4ED6\u4EEC\u5BF9\u5F97\u8D77\u51FA\u94B1\u7ED9\u4ED6\u4EEC\u8BFB\u5927\u5B66\u7684\u7236\u6BCD\uFF0C\u6216\u56E0\u4E3A\u4ED6\u4EEC\u8981\u4F7F\u4E00\u4E2A\u5584\u5F85\u4ED6\u4EEC\u7684\u6559\u5E08\u6B22\u559C\uFF0C\u6216\u5E0C\u671B\u6BD5\u4E1A\u540E\u53EF\u4EE5\u5F97\u5230\u8F83\u9AD8\u7684\u85AA\u4FF8\u4EE5\u517B\u5BB6\u3002</p><p>\u6211\u89C9\u5F97\u8FD9\u4E00\u5207\u7684\u601D\u5FF5\u90FD\u662F\u4E0D\u9053\u5FB7\u7684\uFF0C\u77E5\u8BC6\u8FFD\u6C42\u5E94\u8BE5\u6210\u4E3A\u4E00\u4E2A\u4EBA\u81EA\u5DF1\u7684\u4E8B\uFF0C\u4E0E\u522B\u4EBA\u65E0\u5173\u3002\u53EA\u6709\u8FD9\u6837\uFF0C\u6559\u80B2\u624D\u80FD\u591F\u6210\u4E3A\u4E00\u79CD\u79EF\u6781\u7684\u3001\u6B22\u4E50\u7684\u4E8B\u60C5\u3002</p>",22);function r(l,d){return e(),t("div",null,[_,o(" more "),n])}const f=p(s,[["render",r],["__file","042301.html.vue"]]);export{f as default};
