import{_ as a,o as i,c as n,f as h,a as e,d as c}from"./app.9a681789.js";const p={},t=e("p",null,"\u7528\u6C49\u8BED\u7684\u201C\u4E2D\u56FD\u201D\u6765\u6307\u4EE3\u6211\u4EEC\u8FD9\u4E2A\u56FD\u5BB6\u662F\u4E2D\u534E\u6C11\u56FD\u4EE5\u540E\u7684\u4E8B\u60C5\uFF0C\u5386\u53F2\u4E0A\u7684\u201C\u4E2D\u56FD\u201D\u4EC5\u4EC5\u6307\u4EE3\u4E2D\u539F\u4E00\u5E26\uFF0C\u201C\u4E2D\u56FD\u201D\u672C\u6765\u53EA\u662F\u4E2A\u5730\u7406\u6982\u5FF5\uFF0C\u800C\u4E0D\u662F\u56FD\u5BB6\u7684\u79F0\u547C\u3002\u5176\u5B9E\uFF0C\u2018\u4E2D\u534E\u6C11\u65CF\u2019\u4E5F\u662F\u6C11\u56FD\u624D\u4EA7\u751F\u7684\u6982\u5FF5\uFF0C\u53E4\u4EE3\u6C11\u65CF\u5212\u5206\u662F\u5F88\u4E25\u683C\u7684\uFF0C\u4E0D\u4F1A\u628A\u6C49\u65CF\u548C\u5176\u4ED6\u6E38\u7267\u6C11\u65CF\u6DF7\u4E3A\u4E00\u8C08\u3002\u4E2D\u534E\u6587\u5316\u4E2D\u6CA1\u6709\u6C11\u65CF\u56FD\u5BB6\u7684\u6982\u5FF5\uFF0C\u6C11\u65CF\u56FD\u5BB6\u7684\u6982\u5FF5\u662F\u897F\u65B9\u624D\u6709\u7684\u3002\u4E2D\u56FD\u5386\u53F2\u4E0A\u671D\u4EE3\u66F4\u8FED\uFF0C\u671D\u4EE3\u540D\u5F88\u591A\uFF0C\u4F46\u662F\u771F\u6B63\u4E0D\u53D8\u7684\u56FD\u540D\u53EA\u6709\u4E00\u4E2A\uFF1AChina\u2014\u2014\u2018China\u2019\u5C31\u662F\u4E2D\u56FD\u56FD\u540D\u7684\u8BFB\u97F3\uFF0C\u4E5F\u662F\u4E16\u754C\u4E0A\u7EDD\u5927\u90E8\u5206\u56FD\u5BB6\u627F\u8BA4\u7684\u8BFB\u97F3\u3002",-1),r=c('<h4 id="\u4E2D\u56FD\u81EA\u53E4\u4EE5\u6765\u5C31\u88AB\u6210\u4E3Achina-china-\u8FD9\u4E2A\u8BCD\u6700\u91CD\u8981\u7684\u662F\u5B83\u7684\u8BFB\u97F3\u3002" tabindex="-1"><a class="header-anchor" href="#\u4E2D\u56FD\u81EA\u53E4\u4EE5\u6765\u5C31\u88AB\u6210\u4E3Achina-china-\u8FD9\u4E2A\u8BCD\u6700\u91CD\u8981\u7684\u662F\u5B83\u7684\u8BFB\u97F3\u3002" aria-hidden="true">#</a> \u4E2D\u56FD\u81EA\u53E4\u4EE5\u6765\u5C31\u88AB\u6210\u4E3AChina\uFF0C\u201CChina\u201D\u8FD9\u4E2A\u8BCD\u6700\u91CD\u8981\u7684\u662F\u5B83\u7684\u8BFB\u97F3\u3002</h4><p>\u4E2D\u56FD\u81EA\u53E4\u4EE5\u6765\uFF0C\u5927\u90E8\u5206\u65F6\u95F4\u3001\u88AB\u5927\u90E8\u5206\u56FD\u5BB6\u79F0\u547C\u4E3AChina\u3002\u53EA\u6709\u5C11\u6570\u56FD\u5BB6\u79F0\u4E2D\u56FD\u4E3A\uFF1A\u62D3\u8DCBTabac\u6216\u5951\u4E39Kitai\u3002</p><p>China\u8D77\u6E90\u4E8E\u53E4\u68B5\u6587\u201C\u652F\u90A3\u201D\uFF0C\u521D\u4F5CCina\u3002\u53E4\u7F57\u9A6C\u4EBA\u4E5F\u628A\u4E2D\u56FD\u8BB0\u4F5Ccina\uFF0C\u90A3\u662F\u82F1\u6587china\u7684\u6765\u6E90\uFF0C\u5C31\u662F\u8BF4\u82F1\u8BED\u521A\u51FA\u73B0\u7684\u65F6\u5019\uFF0C\u4E2D\u56FD\u5C31\u5DF2\u7ECF\u88AB\u79F0\u4E3Achina\u4E86\u3002\u65E5\u672C\u79F0\u4E2D\u56FD\u4E3A\u201C\u652F\u90A3\u201D\u59CB\u4E8E\u5510\u4EE3\uFF0C\u65E5\u672C\u50E7\u4FA3\u7FFB\u8BD1\u4E2D\u56FD\u7ECF\u4E66\uFF0C\u5C06\u201C\u4E2D\u56FD\u201D\u97F3\u8BD1\u6210\u65E5\u8BED\u201Csina\u201D\uFF08\u8BFB\u97F3\uFF09\uFF0C\u6240\u4EE5\u4ECE\u6B64\u4EE5\u540E\uFF0C\u65E5\u8BED\u4E2D\u7684\u6C49\u8BED\u5C31\u53EB\u201Csina-Japanese\u201D\uFF0C\u65E5\u672C\u7684\u6C49\u6587\u5316\u5C31\u662F\u201Csina\u201D\u6587\u5316\u3002</p><p>\u4E2D\u56FD\u672C\u6765\u7684\u79F0\u547C\u5C31\u662Fchina\u3002\u6CD5\u8BEDchine\uFF0C\u68B5\u8BEDCi^na\u2014stha^ na\uFF0C\u4EE5\u53CASin, Thin\uFF0C\u6216 Sinai,Thinai\u7B49\u7B49\u90FD\u662F\u5916\u56FD\u4EBA\u5BF9china\u7684\u97F3\u8BD1\u3002\u4E2D\u56FD\u5C31\u662Fchina\uFF0C\u81EA\u53E4\u4EE5\u6765\u5C31\u88AB\u79F0\u4E3Achina\uFF0C\u8FD9\u4E2A\u79F0\u547C\u4E98\u53E4\u6D41\u4F20\u3002\u4E2D\u56FD\u662F\u4E2A\u671D\u4EE3\u66F4\u8FED\u4F46\u56FD\u5BB6\u540D\u79F0\u59CB\u7EC8\u4E0D\u53D8\u7684\u56FD\u5BB6\u3002</p><h4 id="\u56E0\u51FA\u4EA7\u67D0\u79CD\u7279\u8272\u4EA7\u54C1\u800C\u4EE5\u6B64\u4E3A\u540D\u7684\u56FD\u5BB6\u662F\u4E0D\u5B58\u5728\u7684" tabindex="-1"><a class="header-anchor" href="#\u56E0\u51FA\u4EA7\u67D0\u79CD\u7279\u8272\u4EA7\u54C1\u800C\u4EE5\u6B64\u4E3A\u540D\u7684\u56FD\u5BB6\u662F\u4E0D\u5B58\u5728\u7684" aria-hidden="true">#</a> \u56E0\u51FA\u4EA7\u67D0\u79CD\u7279\u8272\u4EA7\u54C1\u800C\u4EE5\u6B64\u4E3A\u540D\u7684\u56FD\u5BB6\u662F\u4E0D\u5B58\u5728\u7684</h4><p>\u4E2D\u56FD\u7684\u897F\u65B9\u540D\u79F0China\u662F\u6307\u201C\u74F7\u5668\u201D\u3001\u201C\u4E1D\u7EF8\u201D\u3001\u201C\u8336\u53F6\u201D\u7B49\u7B49\u5417\uFF1F</p><p>\u4E2D\u56FD\u96BE\u9053\u4F1A\u4EE5\u2018\u67D0\u79CD\u7279\u4EA7\u2019\u4F5C\u4E3A\u56FD\u540D\uFF1F</p><p>\u56E0\u51FA\u4EA7\u67D0\u79CD\u7279\u8272\u4EA7\u54C1\u800C\u4EE5\u6B64\u4E3A\u540D\u7684\u56FD\u5BB6\u662F\u4E0D\u5B58\u5728\u7684\uFF0C\u4F60\u80FD\u627E\u51FA\u8FD9\u6837\u7684\u4F8B\u5B50\u5417\uFF1F\u5982\u6B64\u4F8B\u5B50\u5728\u4E2D\u56FD\u5386\u53F2\u4E0A\u627E\u4E0D\u51FA\u6765\uFF0C\u5728\u4E16\u754C\u5386\u53F2\u4E0A\u4E5F\u627E\u4E0D\u51FA\u6765\u3002\u4E00\u4E2A\u56FD\u5BB6\u6216\u8005\u5730\u533A\u7684\u540D\u79F0\u5FC5\u5B9A\u6765\u81EA\u90A3\u4E2A\u5730\u65B9\u4EBA\u4EEC\u7684\u81EA\u79F0\uFF0C\u7279\u522B\u662F\u7EDF\u6CBB\u8005\u7684\u81EA\u6211\u79F0\u547C\u3002\u4E2D\u56FD\u5386\u53F2\u4E0A\u4ECE\u6765\u6CA1\u6709\u90A3\u4E2A\u7EDF\u6CBB\u8005\u4EE5\u74F7\u5668\u81EA\u79F0\u8FC7\uFF0C\u56E0\u6B64china\u4E0D\u4F1A\u662F\u4ECE\u74F7\u5668\u6765\u7684\u3002\u4E2D\u56FD\u5386\u53F2\u4E0A\u5148\u6709China\u7684\u79F0\u547C\uFF0C\u518D\u6709\u4E2D\u56FD\u7684\u74F7\u5668\u3002\u6240\u4EE5\u74F7\u5668\u4EE5\u4E2D\u56FD\u4E3A\u540D\uFF0C\u800C\u4E0D\u662F\u76F8\u53CD\u3002\u74F7\u5668china\uFF0C\u6765\u81EA\u4E2D\u56FD\u7684\u56FD\u540D\u201CChina\u201D\uFF0C\u800C\u4E0D\u662F\u76F8\u53CD\u3002</p><p>\u4E2D\u56FD\u7684\u897F\u65B9\u540D\u79F0china\u4ECE\u4F55\u800C\u6765\uFF1F\u4E00\u822C\u8BA4\u4E3A\u6765\u81EA\u74F7\u5668\uFF0C\u8BF4\u4E2D\u56FD\u53E4\u4EE3\u7CBE\u7F8E\u74F7\u5668\u8FDC\u9500\u6B27\u6D32\uFF0C\u897F\u65B9\u4EBA\u4FBF\u4EE5\u74F7\u5668\u6307\u79F0\u4E2D\u56FD\uFF0C\u82F1\u8BEDchina\u4E00\u8BCD\u4E5F\u786E\u6709\u201C\u74F7\u5668\u201D\u7684\u610F\u601D\u3002\u90A3\u4E48china\u672C\u4E49\u771F\u662F\u74F7\u5668\u5417\uFF1F\u975E\u4E5F\uFF01\u8FD9\u662F\u91CD\u5927\u7684\u5386\u53F2\u8BEF\u89E3\u548C\u8BB9\u4F20\uFF0CChina\u4E4B\u79F0\u547C\u6765\u81EA\u9065\u8FDC\u7684\u8FC7\u53BB\uFF0C\u4E0E\u534E\u590F\u6587\u660E\u8D77\u6E90\u6709\u5173\u3002</p><p>China\u7684\u5386\u53F2\u8FDC\u8FDC\u4E45\u4E8E\u74F7\u5668\uFF0C\u74F7\u5668\u4EA7\u751F\u5728\u5510\u5B8B\u65F6\u4EE3\uFF0C\u5F00\u59CB\u8FDC\u9500\u6B27\u6D32\u662F\u5728\u5143\u4EE3\uFF0C\u5510\u5B8B\u4E4B\u524D\u4E2D\u56FD\u6CA1\u6709\u74F7\u5668\uFF0C\u53EA\u6709\u9676\u5668\u3002\u53E4\u5370\u5EA6\u68B5\u6587\u53F2\u4E66\u4E5F\u628A\u4E2D\u56FD\u4E4B\u5730\u8BB0\u4F5Ccina\uFF0C\u8BFB\u97F3\u4E0E\u53E4\u4EE3\u610F\u5927\u5229\u8BED\u5B8C\u5168\u4E00\u81F4\uFF0C\u90FD\u6CA1\u6709\u74F7\u5668\u7684\u610F\u601D\u3002\u7531\u6B64\u53EF\u89C1\uFF0Cchina\u4E0D\u4F1A\u662F\u4ECE\u74F7\u5668\u800C\u6765\u7684\uFF0C\u5F88\u8FDF\u4EE5\u540E\u624D\u88AB\u8D4B\u4E88\u4E86\u201C\u74F7\u5668\u201D\u7684\u610F\u601D\u3002\u53E4\u5370\u5EA6\u68B5\u6587\u548C\u53E4\u7F57\u9A6C\u6587\u90FD\u662F\u62FC\u97F3\u6587\u5B57\uFF0C\u6CE8\u97F3\u6BD4\u8F83\u51C6\u786E\uFF0C\u4E24\u8005\u9AD8\u5EA6\u4E00\u81F4\uFF0C\u8BF4\u660E\u8FD9\u4E2A\u56FD\u540D\uFF0C\u786E\u5207\u5730\u8BF4\u5730\u65B9\u4E4B\u540D\uFF0C\u6CE8\u97F3\u6CA1\u6709\u8D70\u6837\u3002\u53E4\u4EE3\u65E5\u672C\u4EBA\u4E0E\u6B27\u6D32\u4EBA\u6CA1\u6709\u673A\u4F1A\u63A5\u89E6\uFF0C\u201C\u652F\u90A3\u201D\u8FD9\u4E2A\u79F0\u547C\u4E0D\u4F1A\u662F\u4ECE\u897F\u65B9\u4F20\u53BB\u7684\uFF0C\u201C\u652F\u90A3\u201D\u4E5F\u6CA1\u6709\u201C\u74F7\u5668\u201D\u7684\u610F\u601D\u3002</p><h4 id="\u6211\u4EE5\u4E3Achina-cina-\u6765\u81EA\u8499\u53E4\u8BED-\u72FC-\u7684\u8BFB\u97F3-\u8D64\u90A3" tabindex="-1"><a class="header-anchor" href="#\u6211\u4EE5\u4E3Achina-cina-\u6765\u81EA\u8499\u53E4\u8BED-\u72FC-\u7684\u8BFB\u97F3-\u8D64\u90A3" aria-hidden="true">#</a> \u6211\u4EE5\u4E3AChina\uFF08cina\uFF09\u6765\u81EA\u8499\u53E4\u8BED\u201C\u72FC\u201D\u7684\u8BFB\u97F3\u201C\u8D64\u90A3\u201D</h4><p>\u65E2\u7136China\u672C\u8EAB\u5C31\u662F\u5916\u56FD\u4EBA\u97F3\u8BD1\uFF0C\u6240\u4EE5China\u662F\u4E2A\u8BFB\u97F3\uFF0C\u90A3\u4E48\u5B83\u5FC5\u7136\u6709\u672C\u6765\u7684\u610F\u4E49\uFF0C\u4E16\u754C\u4E0A\u6CA1\u6709\u201C\u53EA\u6709\u8BFB\u97F3\uFF0C\u6CA1\u6709\u610F\u4E49\u7684\u56FD\u540D\u201D\u3002</p><p>\u6211\u4EE5\u4E3AChina\uFF08cina\uFF09\u6765\u81EA\u8499\u53E4\u8BED\u201C\u72FC\u201D\u7684\u8BFB\u97F3\u201C\u8D64\u90A3\u201D\u3002\u897F\u65B9\u4EBA\u6700\u5148\u63A5\u89E6\u5230\u7684\u4E2D\u56FD\u4EBA\uFF0C\u5C31\u662F\u4E2D\u56FD\u7684\u6E38\u7267\u6C11\u65CF\uFF0C\u4ED6\u4EEC\u628A\u6E38\u7267\u6C11\u65CF\u5D07\u62DC\u7684\u201C\u8D64\u90A3\u201D\uFF08\u72FC\uFF09\u5F53\u505A\u4E2D\u56FD\u7684\u56FD\u540D\u2014\u2014china\uFF08cina\uFF09\u3002</p><p>\u4E3A\u4EC0\u4E48\u897F\u65B9\u4EBA\u6700\u5148\u63A5\u89E6\u5230\u4E2D\u56FD\u7684\u6E38\u7267\u6C11\u65CF\u5462\uFF1F\u5F88\u597D\u7406\u89E3\uFF0C\u4E2D\u56FD\u7684\u519C\u8015\u6C49\u6C11\u65CF\u662F\u5B9A\u5C45\u578B\uFF0C\u96BE\u4EE5\u548C\u5176\u4ED6\u56FD\u5BB6\u7684\u6C11\u65CF\u8FDB\u884C\u6C9F\u901A\u548C\u4EA4\u6D41\u3002\u6C49\u65CF\u662F\u4E2A\u5185\u805A\u3001\u5185\u655B\u7684\u6C11\u65CF\uFF0C\u957F\u671F\u7684\u519C\u8015\u751F\u6D3B\u5851\u9020\u4E86\u6C49\u65CF\u4EBA\u7684\u6027\u683C\uFF1A\u4ED6\u4EEC\u4E0D\u613F\u610F\u8FC1\u5F99\uFF0C\u4E0D\u4E3B\u52A8\u548C\u5176\u4ED6\u6C11\u65CF\u53D1\u751F\u4EA4\u6D41\uFF0C\u559C\u6B22\u8FC7\u5C01\u95ED\u81EA\u5F97\u7684\u751F\u6D3B\u3002\u800C\u4E2D\u56FD\u7684\u6E38\u7267\u6C11\u65CF\u6070\u6070\u76F8\u53CD\uFF1A\u4ED6\u4EEC\u6709\u5F00\u62D3\u65B0\u751F\u5B58\u7A7A\u95F4\u7684\u5F3A\u70C8\u613F\u671B\u548C\u91CE\u5FC3\uFF0C\u4ED6\u4EEC\u6709\u5F88\u5F3A\u7684\u6D41\u52A8\u6027\u3001\u4E50\u4E8E\u8FC1\u5C45\u3001\u5411\u5F80\u81EA\u7531\uFF0C\u4ED6\u4EEC\u4E3B\u52A8\u5730\u5411\u897F\u65B9\u524D\u8FDB\u3002\u2014\u2014\u897F\u65B9\u4EBA\u6700\u5148\u63A5\u89E6\u5230\u7684\u4E2D\u56FD\u4EBA\u5FC5\u7136\u662F\u6E38\u7267\u5C11\u6570\u6C11\u65CF\u800C\u975E\u519C\u8015\u6C49\u6C11\u65CF\u3002</p><p>\u897F\u65B9\u4EBA\u6700\u5148\u63A5\u89E6\u5230\u4E2D\u56FD\u7684\u6E38\u7267\u6C11\u65CF\uFF0C\u6240\u4EE5\u5728\u897F\u65B9\uFF0C\u4E2D\u56FD\u6E38\u7267\u6C11\u65CF\u7684\u6587\u5316\u4EE3\u8868\u7740\u4E2D\u56FD\u7684\u6587\u5316\u3002\u519C\u8015\u6C49\u6C11\u65CF\u521B\u9020\u7684\u6587\u5316\u662F\u5C01\u95ED\u7684\u6587\u5316\uFF0C\u9664\u4E86\u65E5\u672C\u3001\u671D\u9C9C\u7B49\u4E1C\u65B9\u56FD\u5BB6\u4E4B\u5916\uFF0C\u897F\u65B9\u5404\u56FD\u5BF9\u6C49\u6587\u5316\u3001\u5112\u5BB6\u6587\u5316\u51E0\u4E4E\u662F\u4E00\u65E0\u6240\u77E5\u3002</p><p>\u5728\u53E4\u4EE3\uFF0C\u897F\u65B9\u4EBA\u63A5\u89E6\u5230\u4E86\u8FDC\u9053\u800C\u6765\u7684\u6E38\u7267\u6C11\u65CF\uFF0C\u770B\u5230\u4ED6\u4EEC\u9A81\u52C7\u5584\u6218\u3001\u76F8\u8C8C\u660E\u663E\u4E0D\u540C\u4E8E\u897F\u65B9\uFF0C\u4ED6\u4EEC\u5185\u5FC3\u611F\u5230\u975E\u5E38\u9707\u60CA\uFF0C\u5FC5\u7136\u95EE\u4E86\u4E2D\u56FD\u7684\u6E38\u7267\u6C11\u65CF\u4E24\u4E2A\u6700\u57FA\u672C\u7684\u95EE\u9898\uFF1A1\u3001\u4F60\u4EEC\u662F\u4EC0\u4E48\u4EBA\uFF1F2\u3001\u4F60\u4EEC\u4ECE\u54EA\u91CC\u6765\uFF1F\u2014\u2014\u4E0D\u8981\u5C0F\u770B\u8FD9\u4E24\u4E2A\u95EE\u9898\uFF0C\u5BF9\u4E8E\u53E4\u4EBA\u6765\u8BF4\u5F88\u96BE\u56DE\u7B54\uFF0C\u53E4\u4EBA\u5730\u7406\u77E5\u8BC6\u3001\u751F\u7406\u77E5\u8BC6\u6B20\u7F3A\uFF0C\u4E0D\u53EF\u80FD\u56DE\u7B54\u5F97\u5F88\u8BE6\u7EC6\u3001\u5F88\u79D1\u5B66\u3002\u6211\u76F8\u4FE1\u4E2D\u56FD\u7684\u6E38\u7267\u6C11\u65CF\u662F\u8FD9\u4E48\u56DE\u7B54\u7684\uFF1A1\u3001\u6211\u4EEC\u662F\u72FC\u7684\u540E\u4EE3\uFF1B2\u3001\u6211\u4EEC\u6765\u81EA\u4E1C\u65B9\u7684\u571F\u5730\u3002</p><p>\u6E38\u7267\u6C11\u65CF\u7ED9\u897F\u65B9\u4EBA\u7559\u4E0B\u6DF1\u523B\u7684\u5370\u8C61\uFF0C\u5C24\u5176\u662F\u4ED6\u4EEC\u81EA\u79F0\u201C\u72FC\u7684\u540E\u4EE3\u201D\uFF0C\u72FC\u56FE\u817E\u53C8\u6BD4\u6BD4\u7686\u662F\uFF0C\u6E38\u7267\u6C11\u65CF\u53C8\u662F\u201C\u72FC\u6027\u201D\u7684\u6C11\u65CF\u2014\u2014\u201C\u8D64\u90A3\u201D\uFF08\u72FC\uFF09\u5C31\u662F\u6E38\u7267\u6C11\u65CF\u6700\u9C9C\u660E\u7684\u5199\u7167\uFF01\u897F\u65B9\u4EBA\u57FA\u4E8E\u4ED6\u4EEC\u5BF9\u4E1C\u65B9\u4EBA\u7684\u8BA4\u8BC6\uFF0C\u4ED6\u4EEC\u53EA\u77E5\u9053\u4E2D\u56FD\u6709\u201C\u8D64\u90A3\u4EBA\u201D\uFF0C\u4E0D\u77E5\u9053\u8FD8\u6709\u519C\u8015\u6C49\u6C11\u65CF\u3002</p><p>\u897F\u65B9\u7ED9\u9065\u8FDC\u7684\u4E1C\u65B9\u56FD\u5BB6\u53D6\u540D\u4E3Achina\uFF0C\u5C31\u662F\u97F3\u8BD1\u201C\u8D64\u90A3\u201D\uFF08\u72FC\uFF09\uFF0C\u4E00\u70B9\u90FD\u4E0D\u5947\u602A\u3002</p><h4 id="\u4FC4\u7F57\u65AF\u4EBA\u5C06\u4E2D\u56FD\u79F0\u4E3A-\u5951\u4E39-kitan-kitai" tabindex="-1"><a class="header-anchor" href="#\u4FC4\u7F57\u65AF\u4EBA\u5C06\u4E2D\u56FD\u79F0\u4E3A-\u5951\u4E39-kitan-kitai" aria-hidden="true">#</a> \u4FC4\u7F57\u65AF\u4EBA\u5C06\u4E2D\u56FD\u79F0\u4E3A\u201C\u5951\u4E39\u201D (kitan\uFF0Ckitai)</h4><p>\u6211\u4EEC\u53EF\u4EE5\u60CA\u8BB6\u5730\u53D1\u73B0\uFF1A\u4E2D\u56FD\u7684\u56FD\u5BB6\u540D\u79F0\uFF1A\u8D64\u90A3China\u3001\u62D3\u8DCBTabac\u3001\u5951\u4E39Kitai\uFF0C\u4E09\u4E2A\u90FD\u662F\u4E2D\u56FD\u6E38\u7267\u6C11\u65CF\u6587\u5316\u7684\u4EA7\u7269\u3002 \u4EBA\u53E3\u5360\u7EDD\u5927\u591A\u6570\u7684\u6C49\u65CF\u3001\u6C49\u6587\u5316\u53CD\u800C\u5BF9\u4E2D\u56FD\u56FD\u540D\u6CA1\u6709\u5F71\u54CD\u529B\u3002</p><p>\u5728\u4FC4\u8BED\u3001\u5E0C\u814A\u8BED\uFF0C\u4EE5\u53CA\u4E2D\u53E4\u82F1\u8BED\u4E2D\uFF0C\u6574\u4E2A\u4E2D\u56FD\u5747\u88AB\u79F0\u4E3A\u201C\u5951\u4E39\u201D\u3002\u5982\u73B0\u5728\u5728\u4FC4\u8BED\u4E2D\u4E2D\u56FD\u7684\u53D1\u97F3\u662F\u201CKitay\u201D\u3002 \u5F53\u4EE3\u82F1\u8BED\u4E5F\u6709\u7528&quot;Cathay&quot;\u6765\u8868\u793A\u4E2D\u56FD\uFF0C\u5982 Cathay Pacific\uFF08\u56FD\u6CF0\u822A\u7A7A\uFF0C\u201C\u4E2D\u56FD\u592A\u5E73\u6D0B\u822A\u7A7A\u201D\uFF09\u3002</p><p>\u4FC4\u7F57\u65AF\u4EBA\u5C06\u4E2D\u56FD\u79F0\u4E3A\u201C\u5951\u4E39\u201D (kitan\uFF0Ckitai)\uFF0C\u4E0E\u4E2D\u4E9A\u4EBA\u548C\u897F\u65B9\u4EBA\u79F0\u4E2D\u56FD\u4E3A\u201C\u8D64\u90A3\u201D\u51FA\u4E8E\u5171\u540C\u7684\u9053\u7406\u3002\u5F53\u65F6\u7684\u5951\u4E39\u4EBA\u5EFA\u7ACB\u7684\u8FBD\u56FD\uFF0C\u6B66\u529B\u5F3A\u76DB\uFF0C\u63A7\u5236\u7740\u6574\u4E2A\u8499\u53E4\u9AD8\u539F\u53CA\u5176\u5468\u56F4\u5730\u533A\uFF0C\u94C1\u9A91\u6240\u81F3\uFF0C\u6240\u5411\u65E0\u654C\uFF0C\u56E0\u6B64\u4FC4\u7F57\u65AF\u4EBA\u628A\u4E2D\u56FD\u79F0\u4E3A\u201C\u5951\u4E39\u201D\u5E76\u6CBF\u7528\u81F3\u4ECA\u3002</p><p>\u4FC4\u7F57\u65AF\u4EBA\u4E0D\u77E5\u5357\u65B9\u6709\u6C49\u4EBA\u5EFA\u7ACB\u7684\u5B8B\u671D\uFF0C\u53EA\u95FB\u5357\u65B9\u6709\u5951\u4E39\u4EBA\u5EFA\u7ACB\u7684\u8FBD(\u5951\u4E39\u6BD4\u5927\u5B8B\u65E9\u51FA\u73B044\u5E74)\uFF0C\u540C\u6837\uFF0C\u897F\u4E9A\u4EBA\u4E0D\u77E5\u4E1C\u65B9\u6709\u6C49\u6C11\u65CF\uFF0C\u53EA\u77E5\u4E1C\u65B9\u6709\u201C\u8D64\u90A3\u201D\u65CF\uFF0C\u5373\u72FC\u56FE\u817E\u7684\u6E38\u7267\u6C11\u65CF\u3002</p><p>\u4FC4\u56FD\u4EBA\u5E76\u6CA1\u6709\u56E0\u4E3A\u5951\u4E39\u4EBA\u7684\u6D88\u4EA1\u800C\u6539\u53D8\u5BF9\u4E2D\u56FD\u7684\u79F0\u547C\uFF0C\u897F\u65B9\u4EBA\u540C\u6837\u4E5F\u6CA1\u6709\u56E0\u4E3A\u620E\u72C4\u7684\u6D88\u5931\u800C\u6539\u53D8\u5BF9\u4E2D\u56FD\u7684\u79F0\u547C\u3002</p><h4 id="\u5BF9\u4E8E-china-\u8FD9\u4E00\u540D\u79F0\u7684\u6765\u6E90\u4E8E-\u79E6-\u3001-\u8336-\u7684\u8BF4\u6CD5\u90FD\u662F\u9519\u8BEF\u7684\u3002" tabindex="-1"><a class="header-anchor" href="#\u5BF9\u4E8E-china-\u8FD9\u4E00\u540D\u79F0\u7684\u6765\u6E90\u4E8E-\u79E6-\u3001-\u8336-\u7684\u8BF4\u6CD5\u90FD\u662F\u9519\u8BEF\u7684\u3002" aria-hidden="true">#</a> \u5BF9\u4E8E\u201Cchina\u201D\u8FD9\u4E00\u540D\u79F0\u7684\u6765\u6E90\u4E8E\u201C\u79E6\u201D\u3001\u201C\u8336\u201D\u7684\u8BF4\u6CD5\u90FD\u662F\u9519\u8BEF\u7684\u3002</h4><p>\u4E2D\u56FD\u4F7F\u7528\u201Cchina\u201D\u8FD9\u4E2A\u82F1\u6587\u540D\u79F0\u59CB\u4E8E1912\u5E74\uFF0C\u53EF\u662F\u897F\u65B9\u4EBA\u5728\u4E24\u5343\u591A\u5E74\u524D\u5C31\u5DF2\u7ECF\u8BA4\u77E5\u8FD9\u4E2A\u4E1C\u4E9A\u56FD\u5BB6\u7684\u540D\u79F0\u4E86\u3002\u4ECE\u65F6\u95F4\u4E0A\u6765\u770B\uFF0C\u79E6\u671D\u5EFA\u7ACB\u4E8E\u516C\u5143\u524D221\u5E74\u3002\u201Ccina\u201D\u4E00\u8BCD\u5728\u5370\u5EA6\u68B5\u6587\u4E2D\u7684\u51FA\u73B0\u7684\u6700\u8FDF\u65F6\u95F4\u4E5F\u5728\u516C\u5143\u524D5\u4E16\u7EAA\uFF0C\u4E2D\u56FD\u8336\u53F6\u7684\u51FA\u53E3\u4E5F\u8981\u665A\u4E8E\u8FD9\u4E00\u65F6\u671F\u3002\u7531\u6B64\u53EF\u89C1\uFF0C\u5370\u5EA6\u68B5\u6587\u4E2D\u7684\u201Ccina\u201D\u548C\u4E2D\u56FD\u7684\u201C\u8336\u201D\u548C\u4E2D\u56FD\u79E6\u56FD\u7684\u201C\u79E6\u201D\u5E76\u65E0\u5173\u7CFB\u3002</p><h4 id="\u6E38\u7267\u6C11\u65CF\u620E\u72C4\u4EE5\u72FC\u4E3A\u56FE\u817E\u3001\u6253\u7740\u72FC\u65D7\u4E1C\u5F81\u897F\u6218-\u7ED9\u897F\u65B9\u4EBA\u7559\u4E0B\u6DF1\u523B\u5370\u8C61" tabindex="-1"><a class="header-anchor" href="#\u6E38\u7267\u6C11\u65CF\u620E\u72C4\u4EE5\u72FC\u4E3A\u56FE\u817E\u3001\u6253\u7740\u72FC\u65D7\u4E1C\u5F81\u897F\u6218-\u7ED9\u897F\u65B9\u4EBA\u7559\u4E0B\u6DF1\u523B\u5370\u8C61" aria-hidden="true">#</a> \u6E38\u7267\u6C11\u65CF\u620E\u72C4\u4EE5\u72FC\u4E3A\u56FE\u817E\u3001\u6253\u7740\u72FC\u65D7\u4E1C\u5F81\u897F\u6218\uFF0C\u7ED9\u897F\u65B9\u4EBA\u7559\u4E0B\u6DF1\u523B\u5370\u8C61</h4><p>\u53E4\u4EE3\u7684\u620E\u72C4\u6216\u4ED6\u7684\u5148\u4EBA\u6B63\u662F\u64CD\u7740\u8499\u53E4\u8BED\u7684\u4E00\u4E2A\u5E9E\u5927\u7684\u793E\u4F1A\u96C6\u56E2\uFF0C\u56E0\u6B64\uFF0C\u201Ccina\u201D\u4E00\u8BCD\u7684\u542B\u4E49\u5E94\u8BE5\u80FD\u591F\u5728\u4ECA\u5929\u7684\u8499\u53E4\u8BED\u4E2D\u5F97\u5230\u76F8\u5E94\u7684\u89E3\u91CA\u3002\u5728\u8499\u53E4\u8BED\u4E2D\uFF0C\u72FC\u7684\u8BFB\u97F3\u53EF\u8C13\u4E0E\u201Ccina\u201D\u975E\u5E38\u76F8\u8C61\uFF0C\u8BFB\u505A\u201C\u8D64\u90A3\u201D\uFF0C\u610F\u601D\u4E3A\u201C\u72FC\u201D\u3002\u897F\u65B9\u56FD\u5BB6\u4E3A\u4EC0\u4E48\u79F0\u547C\u620E\u72C4\u4E3A\u201C\u8D64\u90A3\u201D\u5373\u201C\u72FC\u201D\u5462\uFF1F\u56E0\u4E3A\u620E\u72C4\u4EE5\u72FC\u4E3A\u56FE\u817E\u3001\u6253\u7740\u72FC\u65D7\u4E1C\u5F81\u897F\u6218\u3002</p><p>\u5728\u300A\u8499\u53E4\u79D8\u53F2\u300B\u7684\u5F00\u7BC7\u7B2C\u4E00\u7AE0\u4FBF\u8BF4\uFF1A\u201C\u5929\u547D\u6240\u751F\u7684\u82CD\u8272\u72FC\u4E0E\u60E8\u767D\u8272\u9E7F\u540C\u6E21\u817E\u5409\u601D\u6C34\u6765\u5230\u65A1\u96BE\u6CB3\u6E90\u7684\u4E0D\u513F\u7F55\u5C71\u524D\uFF0C\u4EA7\u751F\u4E86\u5DF4\u5854\u8D64\u7F55\u201D\u3002\u300A\u56FD\u8BED\xB7\u5468\u8BED\u300B\u8BB0\u8F7D\u7740\uFF1A\u201C\u7A46\u5929\u5B50\u897F\u72E9\u72AC\u620E\uFF0C\u83B7\u5176\u4E94\u738B\uFF0C\u5F97\u56DB\u767D\u72FC\u56DB\u767D\u9E7F\u4EE5\u5F52\u201D\u3002\u53F2\u5B66\u5BB6\u7FE6\u4F2F\u8D5E\u8003\u8BC1\uFF1A\u201C\u767D\u72FC\u767D\u9E7F\u662F\u5F53\u65F6\u7684\u6C0F\u65CF\u201D(\u7FE6\u4F2F\u8D5E\u300A\u4E2D\u56FD\u53F2\u7EB2\u300B\uFF0C\u4E09\u8054\u4E66\u5E97\uFF0C1950\u5E74\u7248)\u3002\u8FD9\u662F\u5F88\u660E\u663E\u7684\u56FE\u817E\u5D07\u62DC\uFF0C\u662F\u4E24\u4E2A\u4EE5\u72FC\u548C\u9E7F\u4E3A\u56FE\u817E\u7684\u59FB\u65CF\u3002</p><p>\u300A\u9B4F\u4E66\xB7\u5217\u4F20\u7B2C\u56DB\u5341\u4E5D\u300B\u8BB0\u8F7D\uFF0C\u201C\u7A81\u53A5\u4E4B\u5148\uFF0C\u5E73\u51C9\u6742\u80E1\u4E5F\uFF0C\u59D3\u963F\u53F2\u90A3\u6C0F\u3002\u540E\u9B4F\u592A\u6B66\u706D\u6CAE\u6E20\u6C0F\uFF0C\u963F\u53F2\u90A3\u4EE5\u4E94\u767E\u5BB6\u5954\u8339\u8339(\u201C\u8339\u8339\u201D\u4E5F\u79F0\u4E3A\u201C\u67D4\u7136\u201D)\uFF0C\u4E16\u5C45\u91D1\u5C71\uFF0C\u5DE5\u4E8E\u94C1\u4F5C\u3002\u91D1\u5C71\u72B6\u5982\u515C\u936A\uFF0C\u4FD7\u547C\u515C\u936A\u4E3A\u2018\u7A81\u53A5\u2019\uFF0C\u56E0\u4EE5\u4E3A\u53F7\u3002\u6216\u4E91\uFF0C\u5176\u5148\u56FD\u4E8E\u897F\u6D77\u4E4B\u4E0A\uFF0C\u4E3A\u90BB\u56FD\u6240\u706D\uFF0C\u7537\u5973\u65E0\u5C11\u957F\u5C3D\u6740\u4E4B\u3002\u81F3\u4E00?\uFF0C\u4E0D\u5FCD\u6740\uFF0C\u5216\u8DB3\u65AD\u81C2\uFF0C\u5F03\u4E8E\u5927\u6CFD\u4E2D\u3002\u6709\u4E00\u725D\u72FC\uFF0C\u6BCF\u8854\u8089\u81F3\u5176\u6240\uFF0C\u6B64?\u56E0\u98DF\u4E4B\uFF0C\u5F97\u4EE5\u4E0D\u6B7B\u3002\u5176\u540E\u9042\u4E0E\u72FC\u4EA4\uFF0C\u72FC\u6709\u5B55\u7109\u3002\u5F7C\u90BB\u56FD\u8005\uFF0C\u590D\u4EE4\u4EBA\u6740\u6B64\uFF0C\u800C\u72FC\u5728\u5176\u4FA7\u3002\u4F7F\u8005\u5C06\u6740\u4E4B\uFF0C\u5176\u72FC\u82E5\u4E3A\u795E\u6240\u51ED\uFF0C?\u7136\u81F3\u4E8E\u6D77\u4E1C\uFF0C\u6B62\u4E8E\u5C71\u4E0A\u3002\u5176\u5C71\u5728\u9AD8\u660C\u897F\u5317\uFF0C\u4E0B\u6709\u6D1E\u7A74\uFF0C\u72FC\u5165\u5176\u4E2D\uFF0C\u9047\u5F97\u5E73\u58E4\u8302\u8349\uFF0C\u5730\u65B9\u4E8C\u767E\u4F59\u91CC\u3002\u5176\u540E\u72FC\u751F\u5341\u7537\uFF0C\u5176\u4E00\u59D3\u963F\u53F2\u90A3\u6C0F\uFF0C\u6700\u8D24\uFF0C\u9042\u4E3A\u541B\u957F\uFF0C\u6545\u7259\u95E8\u5EFA\u72FC\u5934\u7E9B\uFF0C\u793A\u4E0D\u5FD8\u672C\u4E5F\u3002\u201D\u6B64\u8BB0\u8F7D\u4E2D\u7684\u201C\u963F\u53F2\u90A3\u201D\u5C31\u662F\u201C\u8D64\u90A3\u201D\uFF0C\u201C\u72FC\u5934\u7E9B\u201D\u5C31\u662F\u72FC\u5934\u65D7\u3002</p><p>\u5510\u671D\u7684\u738B\u6DAF\u5728\u5176\u8BD7\u4E2D\u5C31\u5199\u5230\uFF1A\u201C\u65CC\u7532\u4ECE\u519B\u4E45\uFF0C\u98CE\u4E91\u8BC6\u9635\u96BE\u3002\u4ECA\u671D\u97E9\u4FE1\u8BA1\uFF0C\u65E5\u4E0B\u65A9\u6210\u5B89\u3002\u71D5\u9894\u591A\u5947\u76F8\uFF0C\u72FC\u5934\u6562\u72AF\u8FB9\u3002\u5BC4\u8A00\u73ED\u5B9A\u8FDC\uFF0C\u6B63\u662F\u7ACB\u529F\u5E74\u3002\u201D\u8FD9\u91CC\u7684\u201C\u72FC\u5934\u201D\u6307\u7684\u6B63\u662F\u6253\u7740\u72FC\u5934\u5FBD\u53F7\u6218\u65D7\u7684\u5317\u65B9\u6E38\u7267\u6C11\u65CF\u3002</p><h4 id="\u4E2D\u56FD\u6E38\u7267\u6C11\u65CF\u5BF9\u897F\u65B9\u7684\u4FB5\u6270\u662F\u4EBA\u7C7B\u5927\u8FC1\u5F99\u7684\u6839\u6E90\u4E4B\u4E00\u3002-\u2014\u2014\u897F\u65B9\u4EBA\u7531\u6B64\u624D\u5F00\u59CB\u548C\u4E2D\u56FD\u8054\u7CFB" tabindex="-1"><a class="header-anchor" href="#\u4E2D\u56FD\u6E38\u7267\u6C11\u65CF\u5BF9\u897F\u65B9\u7684\u4FB5\u6270\u662F\u4EBA\u7C7B\u5927\u8FC1\u5F99\u7684\u6839\u6E90\u4E4B\u4E00\u3002-\u2014\u2014\u897F\u65B9\u4EBA\u7531\u6B64\u624D\u5F00\u59CB\u548C\u4E2D\u56FD\u8054\u7CFB" aria-hidden="true">#</a> \u4E2D\u56FD\u6E38\u7267\u6C11\u65CF\u5BF9\u897F\u65B9\u7684\u4FB5\u6270\u662F\u4EBA\u7C7B\u5927\u8FC1\u5F99\u7684\u6839\u6E90\u4E4B\u4E00\u3002 \u2014\u2014\u897F\u65B9\u4EBA\u7531\u6B64\u624D\u5F00\u59CB\u548C\u4E2D\u56FD\u8054\u7CFB</h4><p>\u4ECE\u516C\u5143\u524D\u4E8C\u5343\u5E74\u521D\u8D77, \u51E0\u4E4E\u5728\u8499\u53E4-\u9752\u85CF\u9AD8\u539F\u4EE5\u897F\u7684\u6574\u4E2A\u6B27\u4E9A\u5927\u9646, \u53D1\u751F\u7740\u4E5F\u8BB8\u662F\u4EBA\u7C7B\u5386\u53F2\u4E0A\u6700\u91CD\u8981\u7684\u4E00\u6B21\u79CD\u65CF\u6269\u5F20, \u8FD9\u5C31\u662F\u5728\u4ECE\u5370\u5EA6\u5230\u897F\u73ED\u7259\u7684\u8FBD\u9614\u571F\u5730\u4E0A, \u539F\u59CB\u5370\u6B27\u4EBA(indo-europeans) \u90FD\u5F00\u59CB\u9010\u6E10\u66FF\u4EE3\u5F53\u5730\u5C45\u6C11\u3002\u4E2D\u592E\u4E9A\u7EC6\u4E9A\u5883\u5185\u7684\u96C5\u5229\u5B89\u5C45\u6C11\uFF0C\u4FBF\u5F00\u59CB\u5411\u4E34\u8FD1\u7684\u5404\u5730\u533A\u65BD\u884C\u538B\u529B\uFF0C\u4EBA\u53E3\u5F00\u59CB\u8FC1\u5F99\uFF0C\u5230\u516C\u5143\u524D1000\u5E74\u65F6\uFF0C\u8FD9\u4E2A\u79FB\u6C11\u8FD0\u52A8\u624D\u8FBE\u5230\u4ED6\u7684\u6700\u9AD8\u6F6E\u3002</p><p>\u96C5\u5229\u5B89\u4EBA\u79BB\u5F00\u4E2D\u592E\u4E9A\u7EC6\u4E9A\u88AB\u8FEB\u8FC1\u5F99\uFF0C\u9EA6\u9AD8\u6587\u8BA4\u4E3A\u662F\u7531\u4E8E\u6C14\u5019\u53D8\u5316\u6240\u81F4\uFF0C\u4F46\u4E5F\u6709\u7684\u5B66\u8005\u8BA4\u4E3A\u662F\u7531\u4E8E\u5357\u4FC4\u8349\u539F\u7684\u6E38\u7267\u6C11\u65CF\u7684\u5165\u4FB5\uFF0C\u4F46\u9EA6\u9AD8\u6587\u540C\u65F6\u63D0\u5230\uFF1A\u5F53\u516C\u5143\u524D1900\u52301500\u5E74\u95F4\uFF0C\u6211\u4EEC\u53D1\u73B0\u6709\u4E00\u7FA4\u201C\u91CE\u86EE\u4EBA\u6C11\u201D\u7A81\u7136\u51FA\u73B0\u5728\u8FD1\u4E1C\uFF0C\u4ED6\u4EEC\u98A0\u8986\u4E86\u5F53\u65F6\u8BE5\u5730\u6240\u5B58\u5728\u7684\u591A\u6570\u5E1D\u56FD\uFF0C\u52A0\u4EE5\u5206\u5272\uFF0C\u800C\u81EA\u5EFA\u738B\u56FD\uFF0C\u2026\u2026</p><p>\u6839\u636E\u7814\u7A76\u7684\u7ED3\u679C\uFF0C\u6653\u5F97\u8FD9\u4E9B\u86EE\u65CF\u5168\u4F53\u6240\u8BF4\u7684\u8BED\u8A00\uFF0C\u548C\u96C5\u5229\u5B89\u79CD\u5E76\u65E0\u5173\u8054\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u9EA6\u9AD8\u6587\u6240\u63D0\u5230\u7684\u201C\u91CE\u86EE\u4EBA\u201D\u7684\u8BED\u8A00\u4E0D\u5C5E\u4E8E\u5370\u6B27\u8BED\u7CFB\uFF0C\u90A3\u4E48\u8FD9\u4E9B\u4EBA\u7684\u8BED\u8A00\u6709\u6781\u5927\u7684\u53EF\u80FD\u662F\u5C5E\u4E8E\u963F\u5C14\u6CF0\u8BED\u7CFB\uFF0C\u5982\u679C\u6B64\u63A8\u65AD\u6210\u7ACB\uFF0C\u8FD9\u4E9B\u201C\u91CE\u86EE\u201D\u4EBA\u65E0\u7591\u662F\u4E1C\u4E9A\u5317\u65B9\u8349\u539F\u4E0A\u7684\u6E38\u7267\u6C11\u65CF\u3002\u57FA\u4E8E\u6E38\u7267\u6C11\u65CF\u5728\u8FD1\u4E1C\u6240\u5C55\u793A\u7684\u201C\u91CE\u86EE\u201D\u884C\u4E3A\uFF0C\u6211\u4EEC\u4E0D\u5F97\u4E0D\u8003\u8651\u96C5\u5229\u5B89\u4EBA\u7684\u8FC1\u5F99\u4E5F\u5E94\u8BE5\u4E0E\u4ED6\u4EEC\u7684\u4E1C\u4E34\u4E2D\u56FD\u7684\u6E38\u7267\u6C11\u65CF\u6709\u76F4\u63A5\u7684\u5173\u7CFB</p>',35);function d(s,o){return i(),n("div",null,[t,h(" more "),r])}const l=a(p,[["render",d],["__file","051201.html.vue"]]);export{l as default};
